import './App.css';
import { useEffect } from "react"

function App() {

  let startButton: any;

  const handleSuccess = (stream: any) => {
    startButton.disabled = true;
    const video = document.querySelector('video');
    if (video) {
      video.srcObject = stream;
    }
    stream.getVideoTracks()[0].addEventListener('ended', () => {
      //errorMsg('The user has ended sharing the screen');
      console.log("🍎 エラーだよっ！")
      startButton.disabled = false;
    });
  }

  const handleError = (error: any) => {
    console.log("errorです", error);
  }

  const errorMsg = (msg: string, error: any) => {
    const errorElement = document.querySelector('#errorMsg');
    errorElement.innerHTML += `<p>${msg}</p>`;
    if (typeof error !== 'undefined') {
      console.error(error);
    }
  }

  const onClick = () => {
    const hoge: any = navigator.mediaDevices.getDisplayMedia()
      .then(handleSuccess, handleError);
  }

  useEffect(() => {

  })

  return (
    <div className="App">
      <header className="App-header">
        <div id="container">
          <video id="gum-local" autoPlay playsInline muted></video>
          <button id="startButton" onClick={onClick}>Start</button>
          <div id="errorMsg"></div>
        </div>
      </header>
    </div>
  );
}

export default App;
