import './App.css';
import { useEffect } from "react"

function App() {

  const startButton: any = document.getElementById("startButton");
  console.log(startButton);
  let localStream = null;

  //startButton.disabled = false;
  const video = document.querySelector('video');
  const handleSuccess = (res: any) => {
    if (video) {
      console.log("steram", res);
      video.srcObject = res;
    }
    /*
    stream.getVideoTracks()[0].addEventListener('ended', () => {
      console.log("🍎 エラーだよっ！")
    });
    */
  }

  const handleError = (error: any) => {
    console.log("errorです", error);
  }

  /*
  const errorMsg = (msg: string, error: any) => {
    const errorElement = document.querySelector('#errorMsg');
    if (errorElement) {
      errorElement.innerHTML += `<p>${msg}</p>`;
    }
    if (typeof error !== 'undefined') {
      console.error(error);
    }
  }
  */

  const playVideo = async (element: any, stream: any) => {
    if (stream) {
      console.log('videoTracks=%d, audoTracks=%d', stream.getVideoTracks().length, stream.getAudioTracks().length);
    }

    if (element.srcObject === stream) {
      console.warn('same stream. skip playVideo ');
      return;
    }

    element.srcObject = stream;
    try {
      await element.play();
    }
    catch (err) {
      console.error(err)
    };
  }

  const onClickStart = async () => {
    const option = { video: { frameRate: 1 }, audio: true };
    try {
      localStream = await navigator.mediaDevices.getDisplayMedia(option);
      playVideo(localVideo, localStream);
      console.log('mediaDevice.getDisplayMedia　Screen Capture OK');
    } catch (err) {
      console.error('mediaDevice.getDisplayMedia() error:', err);
    }

    const hoge: any = navigator.mediaDevices.getDisplayMedia()
      .then((res) => {
        handleSuccess(res);
      })
      .catch((res) => {
        handleError(res);
      });
    console.log(hoge);
  }

  const onClickStop = () => {
    console.log("🍓");
    if (video) {
      video?.pause();
      video.srcObject = null;
    }
  }

  useEffect(() => {
    const startButton: any = document.getElementById("startButton");
    console.log(startButton);
    startButton.disabled = false;
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <div id="container">
          <video id="gum-local" autoPlay playsInline muted></video>
          <button id="startButton" onClick={onClickStart}>Start</button>
          <button id="startButton" onClick={onClickStop}>Stop</button>
          <div id="errorMsg"></div>
        </div>
      </header>
    </div>
  );
}

export default App;
